
public class Evenorodd {

	public static void main(String[] args) {
		int num=666;
		boolean flag =((num&1)==0)?true:false;
		if(flag)
		{
			System.out.println(+num+"is Even number");
		}
		else
		{
			System.out.println(+num+"is Odd number");
		}
		// TODO Auto-generated method stub

	}

}
